import React, { Component } from 'react'

export class Signup extends Component {
    constructor() {
        super()
        this.state = {
            username: "",
            email: "",
            phone: 0,
            country: "",
            city: "",
            contructee: false,
            contructor: false
        }
    }

    handleUsername = (e) => {
        this.setState({
            username: e.target.value
        })
    }

    handleEmail = (e) => {
        this.setState({
            email: e.target.value
        })
    }

    handlePhone = (e) => {
        this.setState({
            phone: e.target.value
        })
    }

    handleCountry = (e) => {
        this.setState({
            country: e.target.value
        })
    }

    handleCity = (e) => {
        this.setState({
            city: e.target.value
        })
    }



    handlePersonSelection = (e) => {
        if (e.target.value === "constructor") {
            this.setState({
                constructee: false,
                contructor: true
            })
        } else {
            this.setState({
                constructee: true,
                contructor: false
            })
        }

    }

    handleFormSubmit = (e) => {
        e.preventDefault()
        console.log("form submitted")
    }

    render() {
        return (
            <div>
                <form onSubmit={(e) => this.handleFormSubmit(e)}>
                    <input type='text' placeholder='username' onChange={(e) => this.handleUsername(e)} value={this.state.username} />
                    <input type='text' placeholder='city' onChange={(e) => this.handleCity(e)} value={this.state.city} />
                    <input type='text' placeholder='country' onChange={(e) => this.handleCountry(e)} value={this.state.country} />
                    <input type='text' placeholder='email' onChange={(e) => this.handleEmail(e)} value={this.state.email} />
                    <input type='number' placeholder='phone' onChange={(e) => this.handlePhone(e)} value={this.state.phone} />
                    <select onChange={(e) => this.handleSelection(e)}>
                        <option value='constructor'>constructor</option>
                        <option value='constructee'></option>
                    </select>
                    {/* <input type='text' placeholder='' onChange={(e) => this.handleEmail(e)} value={this.state.email} /> */}
                    <input type='Submit' value="Submit" />
                </form>

                {this.state.username}
            </div>
        )
    }
}

export default Signup
