import React, { Component } from 'react'
import { NavLink } from 'react-router-dom';

const URL = "http://localhost:3000"

export class NewPostForm extends Component {
    constructor() {
        super();
        this.state = {
            title: "",
            description: "",
            categories: [],
            submitted: false
        }
    }

    handleTitleInput = (e) => {
        this.setState({ title: e.target.value })
    }
    handleDescriptionInput = (e) => {
        this.setState({ description: e.target.value })
    }
    handleSubmittedStatus = () => {
        this.setState({ submitted: !this.state.submitted })
    }

    handlePostFormSubmit = (e) => {
        e.preventDefault()
        this.handleSubmittedStatus()
        let { title, description } = this.state
        fetch(URL + "/posts", {
            method: "POST",
            headers: {
                "Content-Type": "Application/json",
                Accept: "Application/json"
            },
            body: JSON.stringify({
                title: title,
                description: description
            })
        })
            .then(resp => resp.json())
            .then(posts => {
                console.log(posts)
            })
    }


    render() {
        if (this.state.submitted === true) {
            return <h2>Submitted</h2>
        } else {
            // t.string :title
            // t.string :description
            // t.integer :contractee_id
            // t.integer :contractor_id
            // t.string :categories, array: true, default: []
            // t.string :status
            return (
                <div className='space_between_lines'>
                    Create a Post
                <form onSubmit={(e) => this.handlePostFormSubmit(e)} className='space_between_lines'>
                        <label>Title:</label>
                        <input type='text' placeholder='title' id="title" onChange={(e) => this.handleTitleInput(e)} value={this.state.title} required /><br />
                        <label>Description:</label>
                        <input type='text' placeholder='description' id="description" onChange={(e) => this.handleDescriptionInput(e)} value={this.state.description} required /><br />
                        <label>Categories:</label>
                        <input type='text' placeholder='categories' id="categories" onChange={(e) => this.handleCategoriesInput(e)} value={this.state.categories} required /><br />
                        <input type='submit' value="Submit" />
                    </form>
                    {/* {this.state.submitted ? <h2>Submitted</h2> : <></>} */}
                    <NavLink to="/"><button>Cancel</button></NavLink>
                </div>
            )
        }
    }
}

export default NewPostForm
