import React, { Component } from 'react'
import Signup from './Signup'
import Login from './Login'

class HomePage extends Component {
    constructor() {
        super()
        this.state = {
            signup: false,
            login: false,
            user_id: 0,
            loggedInAs: ""
        }
    }

    signUp = () => {
        this.setState({ signup: true })
    }

    login = () => {
        this.setState({ login: true })
    }

    showSignUpPage = () => {
        if (this.state.signup === true) {
            return <Signup onSetCurrentUser={this.setCurrentUser} />
        } else if (this.state.login) {
            return <Login onSetCurrentUser={this.setCurrentUser} />
        } else {
            return <>
                <button onClick={this.signUp}>Signup</button>
                <button onClick={this.login}>Login</button>
            </>
        }
    }

    setCurrentUser = (id, signedInUser) => {
        this.setState({ user_id: id, loggedInAs: signedInUser, login: true })
    }

    render() {
        return (
            <div>
                {this.showSignUpPage()}
            </div>
        )
    }
}

export default HomePage
