import React, { Component } from 'react'

export class Signup extends Component {
    constructor() {
        super()
        this.state = {
            username: "",
            email: "",
            phone: 0,
            country: "",
            city: "",
            contractee: false,
            contractor: false
        }
    }

    handleUsername = (e) => {
        this.setState({
            username: e.target.value
        })
    }

    handleEmail = (e) => {
        this.setState({
            email: e.target.value
        })
    }

    handlePhone = (e) => {
        this.setState({
            phone: e.target.value
        })
    }

    handleCountry = (e) => {
        this.setState({
            country: e.target.value
        })
    }

    handleCity = (e) => {
        this.setState({
            city: e.target.value
        })
    }



    handlePersonSelection = (e) => {
        if (e.target.value === "contractor") {
            this.setState({
                contractee: false,
                contractor: true
            })
        } else {
            this.setState({
                contractee: true,
                contractor: false
            })
        }

    }

    handleFormSubmit = (e) => {
        e.preventDefault()
        console.log("form submitted")
        console.log(this.state.username)
        console.log(this.state.city)
        console.log(this.state.country)
        console.log(this.state.email)
        console.log(this.state.phone)
        console.log(this.state.contractee)
        console.log(this.state.contractor)


    }

    render() {
        return (
            <div>
                <form onSubmit={(e) => this.handleFormSubmit(e)}>
                    <input type='text' placeholder='username' onChange={(e) => this.handleUsername(e)} value={this.state.username} /><br />
                    <input type='text' placeholder='city' onChange={(e) => this.handleCity(e)} value={this.state.city} />
                    <input type='text' placeholder='country' onChange={(e) => this.handleCountry(e)} value={this.state.country} />
                    <input type='text' placeholder='email' onChange={(e) => this.handleEmail(e)} value={this.state.email} />
                    <input type='number' placeholder='phone' onChange={(e) => this.handlePhone(e)} value={this.state.phone} />
                    <select onChange={(e) => this.handlePersonSelection(e)}>
                        <option value='contractee'>Contractee</option>
                        <option value='contractor'>Contractor</option>
                    </select>
                    {/* <input type='text' placeholder='' onChange={(e) => this.handleEmail(e)} value={this.state.email} /> */}
                    <input type='Submit' value="Submit" />
                </form>

                {this.state.username}
            </div>
        )
    }
}

export default Signup
