import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';
// import Home from './Home'

const URL = "http://localhost:3000"

export class SignupForm extends Component {
    // let { onSetCurrentUser, onsignUp, onLoggedIn, signup } = this.props
    // let { username, email, phone, country, city, contractee, contractor, submitted } = this.state
    constructor() {
        super()
        this.state = {
            username: "",
            email: "",
            phone: 0,
            country: "",
            city: "",
            contractee: false,
            contractor: false,
            submitted: false
        }

    }

    handleInputData = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    handlePersonSelection = (e) => {
        if (e.target.value === "contractor") {
            this.setState({
                contractee: false,
                contractor: true
            })
        } else {
            this.setState({
                contractee: true,
                contractor: false
            })
        }
    }

    displayMoreFormOptions = () => {
        if (this.state.contractor) {
            return <> <div>more options</div>  </>
        }
    }

    submittedStatus = () => {
        this.setState({ submitted: true })
    }

    handleFormSubmit = (e) => {
        let { onSetCurrentUser, onSignUp, onLoggedIn } = this.props
        let { username, email, phone, country, city, contractee, contractor } = this.state
        onSignUp()
        onLoggedIn()
        fetch(URL + "/users", {
            method: "POST",
            headers: {
                "Content-Type": "Application/json",
                Accept: "Application/json"
            },
            body: JSON.stringify({
                username: username,
                email: email,
                city: city,
                country: country,
                phone: phone,
                contractee: contractee,
                contractor: contractor
            })
        })
            .then(response => response.json())
            .then(user => {
                console.log(user);
                if (user.contractor) {
                    fetch(URL + "/contractors", {
                        method: "POST",
                        headers: {
                            "Content-Type": "Application/json",
                            Accept: "Application/json"
                        },
                        body: JSON.stringify({
                            user_id: user.id
                        })
                    })
                        .then(response => response.json())
                        .then(currentUser => {
                            onSetCurrentUser("contractor")
                        })
                } else {
                    fetch(URL + "/contractees", {
                        method: "POST",
                        headers: {
                            "Content-Type": "Application/json",
                            Accept: "Application/json"
                        },
                        body: JSON.stringify({
                            user_id: user.id
                        })
                    })
                        .then(response => response.json())
                        .then(currentUser => {
                            onSetCurrentUser("contractee")
                        })
                }
            })
            .then(this.setState({
                username: "",
                email: "",
                phone: 0,
                country: "",
                city: "",
                contractee: false,
                contractor: false
            }))
        this.submittedStatus()
    }

    render() {
        let { signup } = this.props
        let { username, email, phone, country, city, submitted } = this.state
        if (submitted === true || signup == false) {
            return <Redirect to='/' />
        } else {
            return (
                <div className='space_between_lines'>
                    <form onSubmit={(e) => this.handleFormSubmit(e)} className='space_between_lines'>
                        <input type='text' id="username" placeholder='username' onChange={(e) => this.handleInputData(e)} value={username} /><br />
                        <input type='text' id='city' placeholder='city' onChange={(e) => this.handleInputData(e)} value={city} /><br />
                        <input type='text' id='country' placeholder='country' onChange={(e) => this.handleInputData(e)} value={country} /><br />
                        <input type='text' id='email' placeholder='email' onChange={(e) => this.handleInputData(e)} value={email} /><br />
                        <input type='number' id='number' placeholder='phone' onChange={(e) => this.handleInputData(e)} value={phone} /><br />
                        <select onChange={(e) => this.handlePersonSelection(e)}>
                            <option value='contractee' selected>Contractee</option>
                            <option value='contractor'>Contractor</option>
                        </select><br />
                        {/* {this.displayMoreFormOptions()} */}
                        {this.state.contractor ?
                            <>
                                <input type='text' id='email' placeholder='email' onChange={(e) => this.handleInputData(e)} value={email} /><br />
                                <input type='text' id='email' placeholder='email' onChange={(e) => this.handleInputData(e)} value={email} /><br />
                            </>
                            : <div>contractee</div>}
                        <input type='submit' value="Submit" />
                    </form>
                </div>
            )
        }
    }
}

export default SignupForm
