import React, { Component } from 'react'
import Home from '../components/Home'
import Navbar from '../components/Navbar'
import { BrowserRouter as Router, Route } from 'react-router-dom'

const PostsURL = "http://localhost:3000/posts"
class App extends Component {
    constructor() {
        super()
        this.state = {
            signup: false,
            login: false,
            loggedInAs: "",
            loggedIn: false,
            posts: []
        }
    }

    componentDidMount() {
        fetch(PostsURL)
            .then(resp => resp.json())
            .then(posts => {
                console.log(posts)
            })
    }

    signUp = () => {
        this.setState({ signup: !this.state.signup, login: false })
    }

    login = () => {
        this.setState({ login: !this.state.login, signup: false })
    }

    loggedIn = () => {
        this.setState({ loggedIn: true })
    }

    setCurrentUser = (signedInUser) => {
        this.setState({ loggedInAs: signedInUser })
    }

    render() {
        let { signup, login, loggedIn, loggedInAs, posts } = this.state
        return (
            <div>
                <Router>
                    <Navbar onSignUp={this.signUp} onLogin={login} loggedIn={loggedIn} />
                    <Route path="/" render={() => <Home
                        signup={signup}
                        login={login}
                        loggedIn={loggedIn}
                        loggedInAs={loggedInAs}
                        posts={posts}
                        onSignUp={this.signUp}
                        onLogin={this.login}
                        onLoggedIn={this.loggedIn}
                        onSetCurrentUser={this.setCurrentUser}
                    />} />
                </Router>
            </div>
        )
    }
}

export default App
