import React, { Component } from 'react'
import { BrowserRouter as Router, Route } from 'react-router-dom';
import SignupForm from './SignupForm'
import LoginForm from './LoginForm'
import Navbar from './Navbar'

class App extends Component {
    constructor() {
        super()
        this.state = {
            signup: false,
            login: false,
            loggedInAs: "",
            loggedIn: false
        }
    }

    signUp = () => {
        this.setState({ signup: !this.state.signup })
    }

    login = () => {
        this.setState({ login: true })
    }

    loggedIn = () => {
        this.setState({ loggedIn: true })
    }

    displayPage = () => {
        if (this.state.signup === true) {
            return <>
                <Router>
                    <Route exact path="/signup" render={() => <SignupForm onSetCurrentUser={this.setCurrentUser} signUp={this.signUp} loggedIn={this.loggedIn} />} />
                </Router>
            </>
        } else if (this.state.loggedIn) {
            return <LoginForm onSetCurrentUser={this.setCurrentUser} />
        }
    }

    setCurrentUser = (id, signedInUser) => {
        this.setState({ loggedInAs: signedInUser, login: true })
    }

    render() {
        return (
            <div>
                <Router>
                    <Route path="/" render={() => <Navbar signUp={this.signUp} login={this.login} loggedIn={this.state.loggedIn} />} />
                </Router>
                {this.displayPage()}
            </div>
        )
    }
}

export default App
