import React, { Component } from 'react'

const URL = "http://localhost:3000"

export class Signup extends Component {
    constructor() {
        super()
        this.state = {
            username: "",
            email: "",
            phone: 0,
            country: "",
            city: "",
            contractee: false,
            contractor: false
        }
    }

    handleUsername = (e) => {
        this.setState({
            username: e.target.value
        })
    }

    handleEmail = (e) => {
        this.setState({
            email: e.target.value
        })
    }

    handlePhone = (e) => {
        this.setState({
            phone: e.target.value
        })
    }

    handleCountry = (e) => {
        this.setState({
            country: e.target.value
        })
    }

    handleCity = (e) => {
        this.setState({
            city: e.target.value
        })
    }



    handlePersonSelection = (e) => {
        if (e.target.value === "contractor") {
            this.setState({
                contractee: false,
                contractor: true
            })
        } else {
            this.setState({
                contractee: true,
                contractor: false
            })
        }

    }

    handleFormSubmit = (e) => {
        e.preventDefault()
        // console.log("form submitted")
        // console.log(this.state.username)
        // console.log(this.state.city)
        // console.log(this.state.country)
        // console.log(this.state.email)
        // console.log(this.state.phone)
        // console.log(this.state.contractee)
        // console.log(this.state.contractor)
        fetch(URL + "/users", {
            method: "POST",
            headers: {
                "Content-Type": "Application/json",
                Accept: "Application/json"
            },
            body: JSON.stringify({
                username: this.state.username,
                email: this.state.email,
                city: this.state.city,
                country: this.state.country,
                phone: this.state.phone,
                contractee: this.state.contractee,
                contractor: this.state.contractor
            })
        })
            .then(response => response.json())
            .then(user => {
                console.log(user);
                // if (user) {
                //     afterLogIn(user)
                // } else {
                //     alert ("user not registered")
                // }
            })
    }

    render() {
        return (
            <div className='space_between_lines'>
                <form onSubmit={(e) => this.handleFormSubmit(e)} className='space_between_lines'>
                    <input type='text' placeholder='username' onChange={(e) => this.handleUsername(e)} value={this.state.username} /><br /><hr />
                    <input type='text' placeholder='city' onChange={(e) => this.handleCity(e)} value={this.state.city} /><br />
                    <input type='text' placeholder='country' onChange={(e) => this.handleCountry(e)} value={this.state.country} /><br />
                    <input type='text' placeholder='email' onChange={(e) => this.handleEmail(e)} value={this.state.email} /><br />
                    <input type='number' placeholder='phone' onChange={(e) => this.handlePhone(e)} value={this.state.phone} /><br />
                    <select onChange={(e) => this.handlePersonSelection(e)}>
                        <option value="" selected disabled hidden>Choose here</option>
                        <option value='contractee'>Contractee</option>
                        <option value='contractor'>Contractor</option>
                    </select><br />
                    <input type='Submit' value="Submit" />
                </form>
            </div>
        )
    }
}

export default Signup
