const API_ROOT = `http://localhost:3000/api/v1`;
const token = localStorage.getItem('token');

const headers = {
    'Content-Type': 'application/json',
    Accepts: 'application/json',
    Authorization: token
};


const login = data => {
    return fetch(`${API_ROOT}/auth`, {
        method: 'POST',
        headers,
        body: JSON.stringify(data)
    }).then(resp => resp.json());
};

const getCurrentUser = () => {
    console.log("getting current user", headers)
    return fetch(`${API_ROOT}/current_user`, {
        headers
    }).then(res => {
        console.log(res)
        return res.json()
    });
};

const createUser = (data) => {
    fetch(API_ROOT + "/users", {
        method: "POST",
        headers,
        body: JSON.stringify({
            username: data.username,
            email: data.email,
            city: data.city,
            country: data.country,
            phone: data.phone,
            contractee: data.contractee,
            contractor: data.contractor
        })
    })
        .then(response => response.json())
        .then(user => {
            console.log(user);
            if (user.contractor) {
                createContractor(user, data)
                // .then(currentUser => {
                //     onSetCurrentUser("contractor")
                // })
            } else {
                createContractee(user, data)
                //         .then(currentUser => {
                //             onSetCurrentUser("contractee")
                //         })
            }
        })
        .then(this.setState({
            username: "",
            email: "",
            phone: 0,
            country: "",
            city: "",
            contractee: false,
            contractor: false,
            password: ""
        }))
}

const createContractor = (user, data) => {
    fetch(URL + "/contractors", {
        method: "POST",
        headers,
        body: JSON.stringify({
            user_id: user.id,
            projects_completed: 0,
            password: data.password,
            services: data.services
        })
    })
        .then(response => response.json())
}

const createContractee = (user, data) => {
    fetch(URL + "/contractees", {
        method: "POST",
        headers,
        body: JSON.stringify({
            user_id: user.id,
            password: data.password
        })
    })
        .then(response => response.json())
}
export const api = {
    auth: {
        login,
        getCurrentUser
    },
    // paintings: {
    //   getPaintings
    // }
    user: {
        createUser
    }
};
