import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';
import { api } from '../services/api';

const Contractees_URL = "http://localhost:3000/contractees"
const Contractors_URL = "http://localhost:3000/contractors"

export class LoginForm extends Component {
    // let { onSetCurrentUser, onLoggedIn, login, onLogin} = this.props
    // let {loginAs } = this.state
    //  let {email, password} = this.state.fields
    constructor() {
        super()
        this.state = {
            error: false,
            fields: {
                email: '',
                password: ''
            },
            loginAs: "",
        }
    }

    handleChange = e => {
        const newFields = { ...this.state.fields, [e.target.name]: e.target.value };
        this.setState({ fields: newFields });
    };

    formSubmittion = (e) => {
        e.preventDefault()
        let { onSetCurrentUser, onLoggedIn, onLogin } = this.props
        let { loginAs } = this.state
        // this.getUser()
        onSetCurrentUser(loginAs)
        onLoggedIn()
        onLogin()
    }

    handlePersonSelection = (e) => {
        this.setState({ loginAs: e.target.value })
    }

    getUser = () => {
        let { loginAs } = this.state
        if (loginAs === "contractee") {
            fetch(Contractees_URL)
                .then(resp => resp.json())
                .then(contractees => this.getContracteeData(contractees))
        } else if (loginAs === "contractor") {
            fetch(Contractors_URL)
                .then(resp => resp.json())
                .then(contractors => this.getContractorData(contractors))
        }
    }

    getContracteeData = () => {
        // fetch contractees
        // map and see for current user
    }
    getContractorData = () => {
        // fetch contractors
        // map and see for current user
    }

    render() {
        let { login } = this.props
        let { email, password } = this.state.fields
        if (login === false) {
            return <Redirect to='/' />
        } else {
            return (
                <div>
                    <form onSubmit={(e) => this.formSubmittion(e)}>
                        <label>E-mail:</label><br />
                        <input type='text' placeholder='email' onChange={(e) => this.handleChange(e)} value={email} required /><br />
                        <label>password:</label><br />
                        <input type='text' placeholder='email' onChange={(e) => this.handleChange(e)} value={email} required /><br />
                        <select onChange={(e) => this.handlePersonSelection(e)}>
                            <option value="" selected disabled hidden>Choose Here</option>
                            <option value='contractee'>Contractee</option><br />
                            <option value='contractor'>Contractor</option>
                        </select><br />
                        <input type='submit' value="Login" />
                    </form>
                </div>
            )
        }
    }
}

export default LoginForm
