import React, { Component } from 'react'
import Signup from './Signup'
import Login from './Login'
import Navbar from './Navbar'

class HomePage extends Component {
    constructor() {
        super()
        this.state = {
            signup: false,
            login: false,
            loggedInAs: "",
            loggedIn: false
        }
    }

    signUp = () => {
        this.setState({ signup: !this.state.signup })
    }

    login = () => {
        this.setState({ login: true })
    }

    loggedIn = () => {
        this.setState({ loggedIn: true })
    }

    displayPage = () => {
        if (this.state.signup === true) {
            return <Signup onSetCurrentUser={this.setCurrentUser} signUp={this.signUp} loggedIn={this.loggedIn} />
        } else if (this.state.loggedIn) {
            return <Login onSetCurrentUser={this.setCurrentUser} />
        }
    }

    setCurrentUser = (id, signedInUser) => {
        this.setState({ loggedInAs: signedInUser, login: true })
    }

    render() {
        return (
            <div>
                <Navbar signUp={this.signUp} login={this.login} loggedIn={this.state.loggedIn} />
                {this.displayPage()}
            </div>
        )
    }
}

export default HomePage
