import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';

const Contractees_URL = "http://localhost:3000/contractees"
const Contractors_URL = "http://localhost:3000/contractors"

export class LoginForm extends Component {
    constructor() {
        super()
        this.state = {
            email: "",
            loginAs: "",
        }
    }

    formSubmittion = (e) => {
        e.preventDefault()
        // this.getUser()
        console.log("in login")
        this.props.onSetCurrentUser(this.state.loginAs)
        this.props.onLoggedIn()
        this.props.onLogin()
    }

    handleEmail = (e) => {
        this.setState({ email: e.target.value })
    }
    handlePersonSelection = (e) => {
        this.setState({ loginAs: e.target.value })
    }

    getUser = () => {
        if (this.state.loginAs === "contractee") {
            fetch(Contractees_URL)
                .then(resp => resp.json())
                .then(contractees => this.getContracteeData(contractees))
        } else if (this.state.loginAs === "contractor") {
            fetch(Contractors_URL)
                .then(resp => resp.json())
                .then(contractors => this.getContractorData(contractors))
        }
    }

    getContracteeData = () => {
        // fetch contractees
        // map and see for current user
    }
    getContractorData = () => {
        // fetch contractors
        // map and see for current user
    }

    render() {
        if (this.props.login === true) {
            return <Redirect to='/' />
        } else {
            return (
                <div>
                    <form onSubmit={(e) => this.formSubmittion(e)}>
                        <input type='text' placeholder='email' onChange={(e) => this.handleEmail(e)} value={this.state.email} /><br />
                        <select onChange={(e) => this.handlePersonSelection(e)}>
                            <option value="" selected disabled hidden>Select one. Login As:</option>
                            <option value='contractee'>Contractee</option>
                            <option value='contractor'>Contractor</option>
                        </select><br />
                        <input type='submit' value="Login" />
                    </form>
                </div>
            )
        }
    }
}

export default LoginForm
