import React, { Component } from 'react'
import Signup from './Signup'

class HomePage extends Component {
    constructor() {
        super()
        this.state = {
            signup: false,
            user_id: 0,
            loggedInAs: ""
        }
    }

    signUp = () => {
        this.setState({ signup: true })
    }

    showSignUpPage = () => {
        if (this.state.signup === true) {
            return <Signup onSetCurrentUserId={this.setCurrentUserId} />
        } else {
            return <button onClick={this.signUp}>Signup</button>
        }
    }

    setCurrentUserId = (id, signedInUser) => {
        this.setState({ user_id: id, loggedInAs: signedInUser })
    }

    render() {
        return (
            <div>
                {this.showSignUpPage()}
            </div>
        )
    }
}

export default HomePage
