import React, { Component } from 'react'

export class NewPostForm extends Component {
    constructor() {
        super()
        this.state = {
            title: "",
            discription: "",
            category: ""
        }

    }

    handlePostFormSubmit = (e) => {
        console.log(e.id)
    }

    render() {
        return (
            <div className='space_between_lines'>
                <form onSubmit={(e) => this.handlePostFormSubmit(e)} className='space_between_lines'>
                    <input type='text' placeholder='title' id="title" onChange={(e) => this.handleFormData(e)} value={this.state.title} /><br />
                    <input type='text' placeholder='discription' id="discription" onChange={(e) => this.handleFormData(e)} value={this.state.discription} /><br />
                    <input type='text' placeholder='category' id="category" onChange={(e) => this.handleFormData(e)} value={this.state.category} /><br />
                    <input type='submit' value="Submit" />
                </form>
            </div>
        )
    }
}

export default NewPostForm
