import React, { Component } from 'react'
import Signup from './Signup'
import Login from './Login'
import Navbar from './Navbar'

class HomePage extends Component {
    constructor() {
        super()
        this.state = {
            signup: false,
            login: false,
            loggedInAs: ""
        }
    }

    signUp = () => {
        this.setState({ signup: !this.state.signup })
    }

    login = () => {
        this.setState({ login: true })
    }

    showSignUpPage = () => {
        if (this.state.signup === true) {
            return <Signup onSetCurrentUser={this.setCurrentUser} signUp={this.signup} />
            // } else if (this.state.login) {
            //     return <Login onSetCurrentUser={this.setCurrentUser} />
        }
    }

    setCurrentUser = (id, signedInUser) => {
        this.setState({ loggedInAs: signedInUser, login: true })
    }

    render() {
        return (
            <div>
                <Navbar signUp={this.signUp} login={this.login} />
                {this.showSignUpPage()}
            </div>
        )
    }
}

export default HomePage
