    }
    getContractorData = () => {
        // fetch contractors
        // map and see for current user
    }

    render() {
        return (
            <div>
                <form onSubmit={(e) => this.formSubmittion(e)}>
                    <input type='text' placeholder='email' onChange={(e) => this.handleEmail(e)} value={this.state.email} /><br />
                    <select onChange={(e) => this.handlePersonSelection(e)}>
                        <option value="" selected disabled hidden>Select one. Login As:</option>
                        <option value='contractee'>Contractee</option>
                        <option value='contractor'>Contractor</option>
                    </select><br />
                    <input type='submit' value="Login" />
                </form>
            </div>
        )
    }
}

export default LoginForm
