import React, { Component } from 'react'
import { NavLink } from 'react-router-dom';
import PostEditForm from '../containers/PostEditForm'

export class PostDetail extends Component {
    constructor(props) {
        super()
        this.state = {
            edit: false
        }
    }

    // let {post} = this.props
    handleEditPost = () => {
        this.setState({ edit: !this.state.edit })
        let { post } = this.props
        return <PostEditForm post={post} />
    }
    render() {
        let { title, discription, created_at, updated_at } = this.props.post
        return (
            <div>
                Title: {title}<br />
                Discription: {discription}<br />
                Created: {created_at}<br />
                Updated: {updated_at}<br />
                <button onClick={this.handleEditPost}>Edit</button>

                <button>Delete</button>
            </div>
        )
    }
}

export default PostDetail
