import React, { Component } from 'react'

export class Signup extends Component {
    constructor() {
        super()
        this.state = {
            username: ""
        }
    }

    handleUsername = (e) => {
        this.setState({
            username: e.target.value
        })
    }

    render() {
        return (
            <div>
                <form>
                    <input type='text' placeholder='username' onChange={(e) => this.handleUsername(e)} value={this.state.username} />
                </form>
                <button onClick={this.props.handleRe}>Signup</button>
                {this.state.username}
            </div>
        )
    }
}

export default Signup
