import React, { Component } from 'react'
import { BrowserRouter as Router, Route, NavLink, Redirect, withRouter } from 'react-router-dom';
import Home from './Home'

const URL = "http://localhost:3000"

export class SignupForm extends Component {
    constructor() {
        super()
        this.state = {
            username: "",
            email: "",
            phone: 0,
            country: "",
            city: "",
            contractee: false,
            contractor: false,
            submitted: false
        }
    }

    handleUsername = (e) => {
        console.log(e.target.id)
        this.setState({
            username: e.target.value
        })
    }

    handlePersonSelection = (e) => {
        if (e.target.value === "contractor") {
            this.setState({
                contractee: false,
                contractor: true
            })
        } else {
            this.setState({
                contractee: true,
                contractor: false
            })
        }
    }

    handleFormSubmit = (e) => {
        console.log("signed up")
        let { username, email, phone, country, city, contractee, contractor } = this.state
        e.preventDefault()
        this.props.signUp()
        this.props.loggedIn()
        fetch(URL + "/users", {
            method: "POST",
            headers: {
                "Content-Type": "Application/json",
                Accept: "Application/json"
            },
            body: JSON.stringify({
                username: username,
                email: email,
                city: city,
                country: country,
                phone: phone,
                contractee: contractee,
                contractor: contractor
            })
        })
            .then(response => response.json())
            .then(user => {
                console.log(user);
                if (user.contractor) {
                    fetch(URL + "/contractors", {
                        method: "POST",
                        headers: {
                            "Content-Type": "Application/json",
                            Accept: "Application/json"
                        },
                        body: JSON.stringify({
                            user_id: user.id
                        })
                    })
                        .then(response => response.json())
                        .then(currentUser => {
                            this.props.onSetCurrentUser(currentUser.id, "contractor")
                        })
                } else {
                    fetch(URL + "/contractees", {
                        method: "POST",
                        headers: {
                            "Content-Type": "Application/json",
                            Accept: "Application/json"
                        },
                        body: JSON.stringify({
                            user_id: user.id
                        })
                    })
                        .then(response => response.json())
                        .then(currentUser => {
                            this.props.onSetCurrentUser(currentUser.id, "contractee")
                        })
                }
            })
            .then(this.setState({
                username: "",
                email: "",
                phone: 0,
                country: "",
                city: "",
                contractee: false,
                contractor: false
            }))

        // window.location = "/"
    }

    render() {
        return (
            <div className='space_between_lines'>
                <form onSubmit={(e) => this.handleFormSubmit(e)} className='space_between_lines'>
                    <input type='text' id="username" placeholder='username' onChange={(e) => this.handleUsername(e)} value={this.state.username} /><br />
                    <input type='text' id='city' placeholder='city' onChange={(e) => this.handleCity(e)} value={this.state.city} /><br />
                    <input type='text' id='country' placeholder='country' onChange={(e) => this.handleCountry(e)} value={this.state.country} /><br />
                    <input type='text' id='email' placeholder='email' onChange={(e) => this.handleEmail(e)} value={this.state.email} /><br />
                    <input type='number' id='number' placeholder='phone' onChange={(e) => this.handlePhone(e)} value={this.state.phone} /><br />
                    <select onChange={(e) => this.handlePersonSelection(e)}>
                        <option value="" selected disabled hidden>Choose here</option>
                        <option value='contractee'>Contractee</option>
                        <option value='contractor'>Contractor</option>
                    </select><br />
                    <input type='submit' value="Submit" />
                </form>
            </div>
        )
    }
}

export default SignupForm
