import React, { Component } from 'react'
import Signup from './Signup'

class HomePage extends Component {
    constructor() {
        super()
        this.state = {
            signup: false
        }
    }

    signUp = () => {
        this.setState({ signup: true })
    }

    showSignUpPage = () => {
        if (this.state.signup === true) {
            return <Signup />
        }
    }


    render() {
        return (
            <div>
                <div onClick={this.signUp}>Signup</div>
                {this.showSignUpPage()}
            </div>
        )
    }
}

export default HomePage
