import React, { Component } from 'react'
import { Redirect, NavLink } from 'react-router-dom';

export class NewPostForm extends Component {
    constructor() {
        super();
        this.state = {
            title: "",
            discription: ""
        }

    }

    handleTitleInput = (e) => {
        this.setState({ title: e.target.value })
    }
    handleDiscriptionInput = (e) => {
        this.setState({ discription: e.target.value })
    }

    handlePostFormSubmit = (e) => {
        e.preventDefault()
        console.log("Post Submitted")
        let { title, discription } = this.state
        fetch(URL + "/users", {
            method: "POST",
            headers: {
                "Content-Type": "Application/json",
                Accept: "Application/json"
            },
            body: JSON.stringify({
                title: title,
                discription: discription
            })
        })
    }


    render() {
        return (
            <div className='space_between_lines'>
                Create a Post
                <form onSubmit={(e) => this.handlePostFormSubmit(e)} className='space_between_lines'>
                    <input type='text' placeholder='title' id="title" onChange={(e) => this.handleTitleInput(e)} value={this.state.title} /><br />
                    <input type='text' placeholder='discription' id="discription" onChange={(e) => this.handleDiscriptionInput(e)} value={this.state.discription} /><br />
                    <input type='submit' value="Submit" />
                </form>
                <button>Cancel</button>
            </div>
        )
    }
}

export default NewPostForm
