import React, { Component } from 'react'
import { BrowserRouter as Router, Route } from 'react-router-dom';
import SignupForm from './SignupForm'
import LoginForm from './LoginForm'
import Navbar from '../components/Navbar'
import NewPostForm from './NewPostForm'
import Timeline from '../components/Timeline'

const PostsURL = "http://localhost:3000/posts"
class App extends Component {
    constructor() {
        super()
        this.state = {
            signup: false,
            login: false,
            loggedInAs: "",
            loggedIn: false
        }
    }

    fetchPostsData = () => {
        fetch()
    }

    signUp = () => {
        this.setState({ signup: !this.state.signup, login: false })
    }

    login = () => {
        this.setState({ login: !this.state.login, signup: false })
    }

    loggedIn = () => {
        this.setState({ loggedIn: true })
    }

    setCurrentUser = (signedInUser) => {
        this.setState({ loggedInAs: signedInUser })
    }

    render() {
        return (
            <div>
                <Router>
                    <Navbar signUp={this.signUp} login={this.login} loggedIn={this.state.loggedIn} />
                    <Route exact path="/signup" render={() => <SignupForm onSetCurrentUser={this.setCurrentUser} onsignUp={this.signUp} loggedIn={this.loggedIn} signup={this.state.signup} />} />
                    <Route exact path="/login" render={() => <LoginForm onSetCurrentUser={this.setCurrentUser} onLoggedIn={this.loggedIn} login={this.state.login} onLogin={this.login} />} />
                    <Route exact path="/post/new" render={() => <NewPostForm />} />
                    <Route exact path="/timeline" render={() => <Timeline />} />
                </Router>
            </div>
        )
    }
}

export default App
